import React,{Component} from 'react'
import {Link} from 'react-router-dom'
import * as BooksAPI from './BooksAPI'
import BookShelf from './BookSelf'

class BookSelfContainer extends Component{
    constructor(props){
        super(props)
        this.state={
            books:[],
            shelf:''
        }
    }
    // componentDidMount() {
    //     BooksAPI.getAll()
    //     .then(resp => {
    //       this.setState({ books: resp});
    //     });
    //   }

    componentWillMount(){
        //BooksApi.getAll() will return a promise

        BooksAPI.getAll()
        .then(function(allBooks){
            this.setState({books:allBooks});
        })
    }

    render(){
        return (
            <div className="app">
              <div className="list-books">

                <div className="list-books-title">
                  <h1>MyReads</h1>
                </div>

                <div className="list-books-content">  
                  {/* <BookShelf 
                    type="Currently Reading" 
                    books= {this.state.books.filter(function(book){
                            book.shelf==='currentlyReading'
                        })}
                  /> */}
                </div>

                <div className="list-books-content">  
                  {/* <BookShelf type="Want to Read" 
                    books={this.state.books.filter(function(book){
                        book.shelf==='wantToRead'
                    })}
                  /> */}
                </div>

                <div className="list-books-content">  
                  {/* <BookShelf type="Read"
                    books={this.state.books.filter(function(book){
                        book.shelf==='read'
                    })}
                  /> */}
                </div>


                <div className="search-books-results">
                  <div className="open-search">
                    <Link to='/Search'>
                      <button>Add a book</button>
                    </Link>
                  </div>
                </div>
              </div>
            </div>
            )
    }
}

export default BookSelfContainer
