        //       if (res.error)
        //         return this.setState({ results: [] });
        //       else {
        //         const hashTable = {};
        //         res.forEach(book => {
        //         shelf.forEach(book => hashTable[book.id] = book.shelf);
        //         book.shelf = hashTable[book.id] || 'none';
        //       });
        //       return this.setState({results: [res] });
        //   }
        // });
      }

    updateBook = (book, shelf) => {
      BooksAPI.update(book, shelf)
      .then(resp => {
        book.shelf = shelf;
        this.setState( state => ({
          books: state.books.filter(Books => this.id !== book.id).concat({book}) })
      );
    });
    }

    render(){
        return (
      <div className="search-books">
        <div className="search-books-bar">
          <Link className="close-search" to='/'>Close</Link>
          <div className="search-books-input-wrapper">
            <input type="text" placeholder="Search by title or author" 
            onChange={(event) => this.getQuery(event.target.value)} />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
          {
            this.state.results.map((book, key) => <Book updatebook={this.updateBook} key={key} book={book} />)
          }
          </ol>
        </div>
      </div>)
    }
}

export default Search
