import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import * as BooksAPI from './BooksAPI.js';
import Book from './Book.js';


class Search extends Component {
      constructor(props) {
          super(props);
          this.state = {
            books: {},
            results: [],
          }
        }

      componentWillMount() {
        BooksAPI.getAll()
          .then(resp => {
            this.setState({books: resp});
          });
      }

      // getQuery = (query) => {
      //   this.setState({query: query},this.updateSearchedBooks);
      // }

      getQuery = function(query){
          if (query===0 || query === undefined)
            return this.setState({results: [] });
            console.log("Query is "+query);

          BooksAPI.search(query).then(response => {
              if(response.error) 
                console.log("No results found")
                this.setState({results: response.items });
                console.log(response.items);
          })
          .catch(()=>{
              console.log("Got error")
              return this.setState({ results: [] });
          });
      }

    // updateBook = (book, shelf) => {
    //   BooksAPI.update(book, shelf)
    //   .then(resp => {
    //     book.shelf = shelf;
    //     this.setState( state => ({
    //       books: state.books.filter(Books => this.id !== book.id).concat({book}) })
    //   );
    // });
    // }

    render(){
        return (
      <div className="search-books">
        <div className="search-books-bar">
          <Link className="close-search" to='/'>Close</Link>
          <div className="search-books-input-wrapper">
            <input type="text" placeholder="Search by title or author" 
            onChange={(event) => this.getQuery(event.target.value)} />
          </div>
        </div>
        {/* <div className="search-books-results">
          <ol className="books-grid">
          {
            this.state.results.map((book, key) => <Book updatebook={this.updateBook} key={key} book={book} />)
          }
          </ol>
        </div> */}
      </div>)
    }
}

export default Search
